{
    "class": "VirtualConnection",
    "headerVersion": 0,
    "displayName": "OMRS Caching Repository Connector",
    "connectorType": {
        "class": "ConnectorType",
        "headerVersion": 0,
        "type": {
            "class": "ElementType",
            "headerVersion": 0,
            "elementOrigin": "LOCAL_COHORT",
            "elementVersion": 0,
            "elementTypeId": "954421eb-33a6-462d-a8ca-b5709a1bd0d4",
            "elementTypeName": "ConnectorType",
            "elementTypeVersion": 1,
            "elementTypeDescription": "A set of properties describing a type of connector."
        },
        "guid": "41a30f1e-4dad-461b-a21c-eacabb4016cc",
        "qualifiedName": "Egeria:OMRSRepositoryConnector:Cached",
        "displayName": "OMRS Caching Repository Connector",
        "description": " The OMRS caching Repository Connector provides a simple repository implementation that caches metadata in an embedded repository connector.",
        "connectorProviderClassName":"org.odpi.openmetadata.adapters.repositoryservices.caching.repositoryconnector.CachingOMRSRepositoryProxyConnectorProvider"
   },
   "embeddedConnections":[
      {
         "class":"EmbeddedConnection",
         "headerVersion":0,
         "position":0,
         "displayName":"embedded XTDB store",
         "embeddedConnection":{  
           "class": "Connection",
               "connectorType": {
                  "class": "ConnectorType",
                  "connectorProviderClassName": "org.odpi.egeria.connectors.juxt.xtdb.repositoryconnector.XtdbOMRSRepositoryConnectorProvider"
               }
         }
      }
   ]
}

{
    "class": "Connection",
    "connectorType": {
        "class": "ConnectorType",
        "connectorProviderClassName": "org.odpi.egeria.connectors.hms.eventmapper.HMSOMRSRepositoryEventMapperProvider"
    },
    "endpoint": {
        "class": "Endpoint",
        "address": "{{hmsep}}"
    },
    "configurationProperties": {
        "qualifiedNamePrefix": "{{qualifiedNamePrefix}}",
        "refreshTimeInterval": {{refreshTimeInterval}}, 
        "CatalogName": "{{hmsCatalogName}}", 
        "DatabaseName": "{{hmsDatabaseName}}",   
        "MetadataStoreUserId": "{{hmsUserid}}",
        "MetadataStorePassword": "{{hmsPassword}}",
         "endpointAddress": "{{hmssourceep}}",
        "useSSL": {{useSSL}},
        "sendPollEvents": {{sendPollEvents}},
        "sendSchemaTypesAsEntities": {{sendSchemaTypesAsEntities}}

    },
    "guid": "fd923c81-4bfb-445f-a866-2ae85b2bdefa",
    "qualifiedName": "OMRS Hive Metastore Event Mapper Connector",
    "DisplayName": "OMRS Hive Metastore Event Mapper Connector that polls for content."
}
